package org.usfirst.frc3244.CMBA.commands;

import java.util.LinkedList;

import edu.wpi.first.wpilibj.command.Command;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;

import org.usfirst.frc3244.CMBA.Robot;

/**
 *
 */
public class  DriveClearOfTote extends Command {
	
	private static final double toteClearPosition = .35;
	private static LinkedList<Double> fifo = null;
    private static double averagegyro = 0;
    private static boolean sample_gyro = false;
    private static double target_gyro = 0;

    public DriveClearOfTote() {
        // Use requires() here to declare subsystem dependencies
        // eg. requires(chassis);

        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.driveSystem);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
    }

    // Called just before this Command runs the first time
    protected void initialize() {
    	sample_gyro = false;
    	fifo = new LinkedList<Double>();
    	fifo.clear();
    	
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	if(fifo.size()>9){
    		double sum = 0;
    		for(int i =0;i<fifo.size();i++){
    			sum = sum + fifo.get(i);
    		}
    		averagegyro = sum/fifo.size();
    		fifo.removeFirst();
    		
    		if(!sample_gyro){
        		target_gyro = averagegyro;
        		sample_gyro = true;
        	}
    		double kp = SmartDashboard.getNumber("Drive Straight Correction");
    		double correction = (target_gyro - averagegyro)*kp;
        	Robot.driveSystem.driveStraight(-SmartDashboard.getNumber("Dive Clear Tote Speed"),-correction);
    	}
    	fifo.add(new Double(Robot.driveSystem.getGyro()));
    	Robot.driveSystem.driveStraight(-.5,0);
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return Robot.driveSystem.getUSDistance() > toteClearPosition;
    }

    // Called once after isFinished returns true
    protected void end() {
    	Robot.driveSystem.driveStraight(0,0);
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    	end();
    }
}
